import React from 'react';

const ImageWithTextAndRays = () => {
  return (
    <div className="relative w-screen h-[600px] overflow-hidden"> {/* Установим ширину на весь экран и высоту 600px */}
      {/* Контейнер для изображения */}
      <div className="relative w-full h-full"> {/* Занимает всю ширину и высоту родителя */}
        {/* Вставьте ваше изображение здесь */}
        <img
          src="./images/img1.png" // Замените на путь к вашему изображению
          alt="Organic Food"
          className="w-full h-full object-cover" /* Растягиваем изображение на всю ширину и высоту */
        />

        {/* Текст поверх изображения */}
        <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 text-white text-center z-10">
          <h1 className="text-3xl font-bold">NaturalProducts</h1>
          <p className="text-xl">ForLoversofHealthy</p>
          <p className="text-xl">OrganicFood</p>
        </div>

        {/* Лучи */}
        <div
          className="absolute z-0"
          style={{
            top: '0px',
            left: '150px', // Первый луч смещен вправо
            width: '180px',
            height: '100%',
            transform: 'rotate(15.098deg)',
            opacity: 0.08, // Светлее на 2 тона (увеличиваем opacity)
            background: 'linear-gradient(180deg, #FFF 0%, rgba(255, 255, 255, 0.00) 100%)',
          }}
        ></div>
        <div
          className="absolute z-0"
          style={{
            top: '0px',
            left: '850px', // Второй луч смещен еще правее и наложен на первый
            width: '400px',
            height: '100%',
            transform: 'rotate(15.098deg)',
            opacity: 0.06, // Светлее на 2 тона (увеличиваем opacity)
            background: 'linear-gradient(180deg, #FFF 0%, rgba(255, 255, 255, 0.00) 100%)',
          }}
        ></div>
      </div>
    </div>
  );
};

export default ImageWithTextAndRays;
